import React, { useState, useEffect } from 'react';
import { portfolioData } from '../data/portfolioData';
import { FaDownload, FaEnvelope, FaAward, FaCode, FaGraduationCap } from 'react-icons/fa';

const Hero = () => {
  const { personalInfo } = portfolioData;
  const [showResumeModal, setShowResumeModal] = useState(false);
  const [displayText, setDisplayText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const fullText = "I'm Pooja Patel";

  useEffect(() => {
    if (currentIndex < fullText.length) {
      const timer = setTimeout(() => {
        setDisplayText(prev => prev + fullText[currentIndex]);
        setCurrentIndex(prev => prev + 1);
      }, 100);
      return () => clearTimeout(timer);
    }
  }, [currentIndex, fullText]);

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleViewResume = () => {
    setShowResumeModal(true);
  };

  const handleDownloadResume = () => {
    const link = document.createElement('a');
    link.href = '/resume/poojaresume.pdf';
    link.download = 'Pooja_Patel_Resume.pdf';
    link.click();
    setShowResumeModal(false);
  };

  const handleCloseModal = () => {
    setShowResumeModal(false);
  };

  return (
    <>
      <section id="home" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50 pt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 w-full">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
            {/* Left Column */}
            <div className="text-center lg:text-left">
              <div className="mb-8">
                <h1 className="text-4xl lg:text-6xl font-bold text-gray-900 mb-6 leading-tight">
                  Hi, <span className="text-primary">{displayText}</span>
                  {currentIndex < fullText.length && (
                    <span className="animate-pulse">|</span>
                  )}
                </h1>
                <h2 className="text-2xl lg:text-3xl text-gray-600 mb-6 font-semibold bg-gradient-to-r from-gray-600 to-gray-800 bg-clip-text text-transparent">
                  {personalInfo.title}
                </h2>
                <p className="text-xl text-gray-600 mb-8 leading-relaxed max-w-2xl">
                  Passionate MERN Stack Developer & 4th Year Computer Science Student 
                  crafting innovative digital solutions with modern technologies
                </p>
              </div>
              
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start mb-12">
                <button
                  onClick={handleViewResume}
                  className="bg-primary hover:bg-blue-700 text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-xl flex items-center justify-center space-x-3 group"
                >
                  <FaDownload className="group-hover:animate-bounce text-lg" />
                  <span className="text-lg">View Resume</span>
                </button>
                <button
                  onClick={() => scrollToSection('contact')}
                  className="border-2 border-primary text-primary hover:bg-primary hover:text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 transform hover:scale-105 flex items-center justify-center space-x-3 group"
                >
                  <FaEnvelope className="group-hover:scale-110 text-lg" />
                  <span className="text-lg">Get In Touch</span>
                </button>
              </div>

              {/* Quick Stats */}
              <div className="flex justify-center lg:justify-start space-x-8">
                <div className="text-center group transform hover:scale-105 transition-transform duration-300">
                  <div className="w-16 h-16 bg-blue-100 rounded-2xl flex items-center justify-center mx-auto mb-3 group-hover:bg-blue-200 transition-colors duration-300">
                    <FaGraduationCap className="text-primary text-2xl" />
                  </div>
                  <div className="text-xl font-bold text-primary">4th Year</div>
                  <div className="text-sm text-gray-600 font-medium">CSE Student</div>
                </div>
                <div className="text-center group transform hover:scale-105 transition-transform duration-300">
                  <div className="w-16 h-16 bg-green-100 rounded-2xl flex items-center justify-center mx-auto mb-3 group-hover:bg-green-200 transition-colors duration-300">
                    <FaAward className="text-green-600 text-2xl" />
                  </div>
                  <div className="text-xl font-bold text-green-600">A+</div>
                  <div className="text-sm text-gray-600 font-medium">Intern Grade</div>
                </div>
                <div className="text-center group transform hover:scale-105 transition-transform duration-300">
                  <div className="w-16 h-16 bg-purple-100 rounded-2xl flex items-center justify-center mx-auto mb-3 group-hover:bg-purple-200 transition-colors duration-300">
                    <FaCode className="text-purple-600 text-2xl" />
                  </div>
                  <div className="text-xl font-bold text-purple-600">MERN</div>
                  <div className="text-sm text-gray-600 font-medium">Full Stack</div>
                </div>
              </div>
            </div>

            {/* Right Column - Enhanced Profile Image */}
            <div className="flex justify-center lg:justify-end">
              <div className="relative group">
                <div className="w-80 h-80 lg:w-96 lg:h-96 bg-gradient-to-br from-primary to-purple-600 rounded-full flex items-center justify-center shadow-2xl transform group-hover:scale-105 transition-all duration-500">
                  <div className="w-72 h-72 lg:w-88 lg:h-88 bg-white rounded-full overflow-hidden border-4 border-white shadow-2xl">
                    <img
                      src="/about/college-photo.jpg"
                      alt="Pooja Patel - MERN Stack Developer"
                      className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-700"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                    {/* Fallback if image doesn't load */}
                    <div className="hidden w-full h-full items-center justify-center flex-col bg-gradient-to-br from-blue-100 to-purple-100 text-gray-600">
                      <div className="text-5xl mb-3">👩‍💻</div>
                      <p className="font-bold text-lg">Pooja Patel</p>
                      <p className="text-gray-500">MERN Stack Developer</p>
                    </div>
                  </div>
                </div>
                
                {/* Enhanced Floating Elements */}
                <div className="absolute -top-4 -left-4 bg-white rounded-2xl shadow-2xl p-4 transform rotate-6 hover:scale-110 transition-all duration-300 group cursor-pointer">
                  <div className="text-center">
                    <div className="text-blue-500 text-2xl mb-2">
                      <FaCode />
                    </div>
                    <div className="text-xs text-gray-700 font-semibold">MERN Stack</div>
                    <div className="text-xs text-gray-500">Developer</div>
                  </div>
                </div>
                
                <div className="absolute -bottom-4 -right-4 bg-white rounded-2xl shadow-2xl p-4 transform -rotate-6 hover:scale-110 transition-all duration-300 group cursor-pointer">
                  <div className="text-center">
                    <div className="text-green-500 text-2xl mb-2">
                      <FaAward />
                    </div>
                    <div className="text-xs text-gray-700 font-semibold">A+ Grade</div>
                    <div className="text-xs text-gray-500">Softpro India</div>
                  </div>
                </div>

                {/* Additional Floating Element */}
                <div className="absolute top-8 -right-6 bg-white rounded-2xl shadow-2xl p-3 transform rotate-12 hover:scale-110 transition-all duration-300 group cursor-pointer">
                  <div className="text-center">
                    <div className="text-orange-500 text-xl mb-1">
                      <FaGraduationCap />
                    </div>
                    <div className="text-xs text-gray-700 font-semibold">4th Year</div>
                    <div className="text-xs text-gray-500">CSE</div>
                  </div>
                </div>

                {/* Enhanced Background Glow */}
                <div className="absolute -inset-6 bg-gradient-to-r from-primary to-purple-600 rounded-full opacity-0 group-hover:opacity-15 blur-2xl transition-opacity duration-700 -z-10"></div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Enhanced Resume Modal */}
      {showResumeModal && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl w-full max-w-5xl max-h-[90vh] overflow-hidden shadow-2xl">
            <div className="flex justify-between items-center p-6 border-b bg-gray-50">
              <h3 className="text-xl font-bold text-gray-900">Pooja Patel - Resume</h3>
              <button
                onClick={handleCloseModal}
                className="text-gray-500 hover:text-gray-700 text-2xl font-bold w-10 h-10 flex items-center justify-center hover:bg-gray-200 rounded-full transition-colors duration-300"
              >
                ×
              </button>
            </div>
            <div className="p-6 h-full">
              <iframe
                src="/resume/poojaresume.pdf"
                className="w-full h-96 lg:h-[500px] border-2 border-gray-200 rounded-lg"
                title="Pooja Patel Resume"
              />
              <div className="flex justify-end space-x-4 mt-6 pt-6 border-t">
                <button
                  onClick={handleCloseModal}
                  className="px-6 py-3 text-gray-600 hover:text-gray-800 font-medium transition-colors duration-300 border border-gray-300 rounded-lg hover:border-gray-400"
                >
                  Close Preview
                </button>
                <button
                  onClick={handleDownloadResume}
                  className="bg-primary hover:bg-blue-700 text-white font-semibold py-3 px-8 rounded-lg transition-all duration-300 flex items-center space-x-3 hover:scale-105"
                >
                  <FaDownload />
                  <span>Download Resume</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Hero;
