import React, { useState } from 'react';
import { portfolioData } from '../data/portfolioData';

const Education = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  const openModal = (imageSrc, altText) => {
    setSelectedImage({ src: imageSrc, alt: altText });
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <section id="education" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-4xl font-bold text-gray-900 mb-4">Education</h2>
          <div className="w-24 h-1 bg-primary mx-auto mb-4"></div>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            My academic journey and qualifications
          </p>
        </div>

        <div className="max-w-4xl mx-auto">
          {portfolioData.education.map((edu, index) => (
            <div key={index} className="relative pl-8 pb-12 last:pb-0">
              {/* Timeline line */}
              {index !== portfolioData.education.length - 1 && (
                <div className="absolute left-4 top-4 w-0.5 h-full bg-primary/30"></div>
              )}
              
              {/* Timeline dot */}
              <div className="absolute left-0 top-4 w-8 h-8 bg-primary rounded-full border-4 border-white shadow-lg flex items-center justify-center">
                <span className="text-white text-sm font-bold">{index + 1}</span>
              </div>
              
              {/* Content */}
              <div className="bg-gray-50 rounded-lg p-6 shadow-md hover:shadow-lg transition-shadow duration-300 border-l-4 border-primary">
                <div className="flex flex-col lg:flex-row gap-6">
                  {/* Text Content */}
                  <div className="flex-1">
                    <div className="flex flex-col md:flex-row md:justify-between md:items-start mb-4">
                      <h3 className="text-xl font-semibold text-gray-900 mb-2">
                        {edu.degree}
                      </h3>
                      <span className="bg-primary text-white px-3 py-1 rounded-full text-sm font-medium mb-2 md:mb-0">
                        {edu.year}
                      </span>
                    </div>
                    <h4 className="text-lg text-secondary font-medium mb-3">
                      {edu.institution}
                    </h4>
                    <div className="flex items-center space-x-4 mb-3">
                      <span className="text-gray-700 font-medium bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm">
                        {edu.score}
                      </span>
                    </div>
                    <p className="text-gray-600 leading-relaxed mb-4">{edu.details}</p>
                  </div>

                  {/* Photo Section - Clickable */}
                  <div className="lg:w-48 lg:h-32 flex-shrink-0">
                    <div 
                      className="bg-gray-100 rounded-lg overflow-hidden shadow-md h-full cursor-pointer transform hover:scale-105 transition-all duration-300"
                      onClick={() => openModal(edu.photo, `${edu.degree} Certificate`)}
                    >
                      <img
                        src={edu.photo}
                        alt={`${edu.degree} Certificate`}
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      {/* Fallback if image doesn't load */}
                      <div className="hidden w-full h-full items-center justify-center flex-col text-gray-500 p-2 text-center">
                        <div className="text-2xl mb-1">📸</div>
                        <div className="text-xs">
                          {edu.degree.includes('Class 10') && 'Class 10 Marksheet'}
                          {edu.degree.includes('Class 12') && 'Class 12 Marksheet'}
                          {edu.degree.includes('Computer Science') && 'College ID/Photo'}
                        </div>
                        <div className="text-xs text-blue-500 mt-1">Click to add photo</div>
                      </div>
                    </div>
                    <p className="text-xs text-gray-500 text-center mt-2">
                      {edu.degree.includes('Class 10') && 'Class 10 Marksheet'}
                      {edu.degree.includes('Class 12') && 'Class 12 Marksheet'}
                      {edu.degree.includes('Computer Science') && 'College ID Card'}
                      <span className="text-blue-500 block">(Click to view)</span>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Photo Instructions */}
        <div className="max-w-4xl mx-auto mt-8 bg-blue-50 rounded-lg p-6">
          <h4 className="text-lg font-semibold text-blue-900 mb-3">📸 How to Add Photos:</h4>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-blue-700">College Photo</div>
              <div className="text-blue-600">File: college-id.jpg</div>
              <div className="text-gray-600 text-xs mt-1">Click photo to view full size</div>
            </div>
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-blue-700">Class 12 Marksheet</div>
              <div className="text-blue-600">File: class12-marksheet.jpg</div>
              <div className="text-gray-600 text-xs mt-1">Click photo to view full size</div>
            </div>
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-blue-700">Class 10 Marksheet</div>
              <div className="text-blue-600">File: class10-marksheet.jpg</div>
              <div className="text-gray-600 text-xs mt-1">Click photo to view full size</div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal for Full Screen Image */}
      {selectedImage && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-90 z-50 flex items-center justify-center p-4"
          onClick={closeModal}
        >
          <div className="relative max-w-4xl max-h-full">
            {/* Close Button */}
            <button
              onClick={closeModal}
              className="absolute -top-12 right-0 text-white text-3xl hover:text-gray-300 transition-colors duration-200 z-10"
            >
              ✕
            </button>
            
            {/* Image */}
            <img
              src={selectedImage.src}
              alt={selectedImage.alt}
              className="max-w-full max-h-[90vh] object-contain rounded-lg"
              onClick={(e) => e.stopPropagation()} // Prevent closing when clicking image
            />
            
            {/* Image Info */}
            <div className="absolute bottom-4 left-4 right-4 bg-black bg-opacity-50 text-white p-3 rounded-lg">
              <p className="text-center text-sm">{selectedImage.alt}</p>
            </div>

            {/* Download Button */}
            <a
              href={selectedImage.src}
              download
              className="absolute top-4 right-4 bg-white text-black px-4 py-2 rounded-lg hover:bg-gray-200 transition-colors duration-200 flex items-center space-x-2"
              onClick={(e) => e.stopPropagation()}
            >
              <span>📥</span>
              <span>Download</span>
            </a>
          </div>
        </div>
      )}
    </section>
  );
};

export default Education;
