import React, { useState } from 'react';
import { portfolioData } from '../data/portfolioData';
import { FaDownload, FaEnvelope, FaAward, FaCode, FaGraduationCap } from 'react-icons/fa';

const Hero = () => {
  const { personalInfo } = portfolioData;
  const [showResumeModal, setShowResumeModal] = useState(false);

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleViewResume = () => {
    setShowResumeModal(true);
  };

  const handleDownloadResume = () => {
    const link = document.createElement('a');
    link.href = '/resume/poojaresume.pdf';
    link.download = 'Pooja_Patel_Resume.pdf';
    link.click();
    setShowResumeModal(false);
  };

  const handleCloseModal = () => {
    setShowResumeModal(false);
  };

  return (
    <>
      <section id="home" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50 pt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 w-full">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            {/* Left Column */}
            <div className="text-center lg:text-left">
              <div className="mb-8">
                <h1 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-4 leading-tight">
                  Hi, I'm <span className="text-primary">{personalInfo.name}</span>
                </h1>
                <h2 className="text-xl lg:text-2xl text-gray-600 mb-6 font-semibold">
                  {personalInfo.title}
                </h2>
                <p className="text-lg text-gray-600 mb-8 leading-relaxed max-w-2xl">
                  4th Year CSE Student | MERN Stack Developer | Passionate about creating innovative web solutions
                </p>
              </div>
              
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start mb-8">
                <button
                  onClick={handleViewResume}
                  className="bg-primary hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg flex items-center justify-center space-x-2 group"
                >
                  <FaDownload className="group-hover:animate-bounce" />
                  <span>View Resume</span>
                </button>
                <button
                  onClick={() => scrollToSection('contact')}
                  className="border-2 border-primary text-primary hover:bg-primary hover:text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 flex items-center justify-center space-x-2 group"
                >
                  <FaEnvelope className="group-hover:scale-110" />
                  <span>Contact Me</span>
                </button>
              </div>

              {/* Quick Stats */}
              <div className="flex justify-center lg:justify-start space-x-8">
                <div className="text-center group">
                  <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-2 group-hover:scale-110 transition-transform duration-300">
                    <FaGraduationCap className="text-primary text-xl" />
                  </div>
                  <div className="text-lg font-bold text-primary">4th Year</div>
                  <div className="text-sm text-gray-600">CSE Student</div>
                </div>
                <div className="text-center group">
                  <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-2 group-hover:scale-110 transition-transform duration-300">
                    <FaAward className="text-green-600 text-xl" />
                  </div>
                  <div className="text-lg font-bold text-green-600">A+</div>
                  <div className="text-sm text-gray-600">Intern Grade</div>
                </div>
                <div className="text-center group">
                  <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-2 group-hover:scale-110 transition-transform duration-300">
                    <FaCode className="text-purple-600 text-xl" />
                  </div>
                  <div className="text-lg font-bold text-purple-600">MERN</div>
                  <div className="text-sm text-gray-600">Stack Dev</div>
                </div>
              </div>
            </div>

            {/* Right Column - Profile Image */}
            <div className="flex justify-center lg:justify-end">
              <div className="relative group">
                <div className="w-64 h-64 lg:w-72 lg:h-72 bg-gradient-to-br from-primary to-purple-500 rounded-full flex items-center justify-center shadow-2xl transform group-hover:scale-105 transition-all duration-500">
                  <div className="w-56 h-56 lg:w-64 lg:h-64 bg-white rounded-full overflow-hidden border-4 border-white shadow-lg">
                    <img
                      src="/about/college-photo.jpg"
                      alt="Pooja Patel - CSE Student"
                      className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-500"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                    {/* Fallback if image doesn't load */}
                    <div className="hidden w-full h-full items-center justify-center flex-col bg-gradient-to-br from-blue-100 to-purple-100 text-gray-600">
                      <div className="text-4xl mb-2">👩‍💻</div>
                      <p className="font-semibold">Pooja Patel</p>
                      <p className="text-sm">CSE Student</p>
                    </div>
                  </div>
                </div>
                
                {/* Floating Elements */}
                <div className="absolute -top-2 -left-2 bg-white rounded-lg shadow-lg p-3 transform rotate-6 hover:scale-110 transition-transform duration-300 group">
                  <div className="text-center">
                    <div className="text-blue-500 text-lg mb-1">
                      <FaCode />
                    </div>
                    <div className="text-xs text-gray-600 font-medium">MERN Stack</div>
                  </div>
                </div>
                
                <div className="absolute -bottom-2 -right-2 bg-white rounded-lg shadow-lg p-3 transform -rotate-6 hover:scale-110 transition-transform duration-300 group">
                  <div className="text-center">
                    <div className="text-green-500 text-lg mb-1">
                      <FaAward />
                    </div>
                    <div className="text-xs text-gray-600 font-medium">Softpro</div>
                  </div>
                </div>

                {/* Background Glow */}
                <div className="absolute -inset-4 bg-gradient-to-r from-primary to-purple-500 rounded-full opacity-0 group-hover:opacity-10 blur-xl transition-opacity duration-500 -z-10"></div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Resume Modal */}
      {showResumeModal && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg w-full max-w-4xl max-h-[90vh] overflow-hidden">
            <div className="flex justify-between items-center p-4 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Pooja Patel - Resume</h3>
              <button
                onClick={handleCloseModal}
                className="text-gray-500 hover:text-gray-700 text-2xl font-bold"
              >
                ×
              </button>
            </div>
            <div className="p-4 h-full">
              <iframe
                src="/resume/poojaresume.pdf"
                className="w-full h-96 lg:h-[500px] border rounded"
                title="Pooja Patel Resume"
              />
              <div className="flex justify-end space-x-4 mt-4 pt-4 border-t">
                <button
                  onClick={handleCloseModal}
                  className="px-4 py-2 text-gray-600 hover:text-gray-800 font-medium transition-colors duration-300"
                >
                  Close
                </button>
                <button
                  onClick={handleDownloadResume}
                  className="bg-primary hover:bg-blue-700 text-white font-semibold py-2 px-6 rounded-lg transition-all duration-300 flex items-center space-x-2"
                >
                  <FaDownload />
                  <span>Download Resume</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Hero;
