import React from 'react';
import { portfolioData } from '../data/portfolioData';

const Hero = () => {
  const { personalInfo } = portfolioData;

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  const downloadResume = () => {
    const link = document.createElement('a');
    link.href = '/resume.pdf';
    link.download = 'Pooja_Patel_Resume.pdf';
    link.click();
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50 pt-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 w-full">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
          {/* Left Column */}
          <div className="text-center lg:text-left animate-fade-in-left">
            <div className="mb-6">
              <h1 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-4 leading-tight">
                Hi, I'm <span className="text-primary">{personalInfo.name}</span>
              </h1>
              <h2 className="text-xl lg:text-2xl text-gray-600 mb-6 font-semibold">
                {personalInfo.title}
              </h2>
              <p className="text-lg text-gray-600 mb-8 leading-relaxed max-w-2xl">
                4th Year CSE Student | MERN Stack Developer | Passionate about creating innovative web solutions
              </p>
            </div>
            
            <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
              <button
                onClick={downloadResume}
                className="bg-primary hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg flex items-center justify-center space-x-2"
              >
                <span>📄</span>
                <span>Download Resume</span>
              </button>
              <button
                onClick={() => scrollToSection('contact')}
                className="border-2 border-primary text-primary hover:bg-primary hover:text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 flex items-center justify-center space-x-2"
              >
                <span>💬</span>
                <span>Contact Me</span>
              </button>
            </div>

            {/* Quick Stats */}
            <div className="flex justify-center lg:justify-start space-x-6 mt-8">
              <div className="text-center">
                <div className="text-xl font-bold text-primary">4th Year</div>
                <div className="text-sm text-gray-600">CSE Student</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-bold text-green-600">A+</div>
                <div className="text-sm text-gray-600">Intern Grade</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-bold text-purple-600">MERN</div>
                <div className="text-sm text-gray-600">Stack Dev</div>
              </div>
            </div>
          </div>

          {/* Right Column - Profile Image */}
          <div className="flex justify-center lg:justify-end">
            <div className="relative group">
              <div className="w-72 h-72 lg:w-80 lg:h-80 bg-gradient-to-br from-primary to-purple-500 rounded-full flex items-center justify-center shadow-2xl transform group-hover:scale-105 transition-all duration-500">
                <div className="w-64 h-64 lg:w-72 lg:h-72 bg-white rounded-full overflow-hidden border-4 border-white shadow-lg">
                  <img
                    src="/about/college-photo.jpg"
                    alt="Pooja Patel - CSE Student"
                    className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-500"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                  {/* Fallback if image doesn't load */}
                  <div className="hidden w-full h-full items-center justify-center flex-col bg-gradient-to-br from-blue-100 to-purple-100 text-gray-600">
                    <div className="text-5xl mb-2">👩‍💻</div>
                    <p className="font-semibold">Pooja Patel</p>
                    <p className="text-sm">CSE Student</p>
                  </div>
                </div>
              </div>
              
              {/* Floating Elements */}
              <div className="absolute -top-2 -left-2 bg-white rounded-lg shadow-lg p-3 transform rotate-6 hover:scale-110 transition-transform duration-300">
                <div className="text-center">
                  <div className="text-lg font-bold text-primary">🚀</div>
                  <div className="text-xs text-gray-600">MERN Stack</div>
                </div>
              </div>
              
              <div className="absolute -bottom-2 -right-2 bg-white rounded-lg shadow-lg p-3 transform -rotate-6 hover:scale-110 transition-transform duration-300">
                <div className="text-center">
                  <div className="text-lg font-bold text-green-600">A+</div>
                  <div className="text-xs text-gray-600">Softpro</div>
                </div>
              </div>

              {/* Background Glow */}
              <div className="absolute -inset-4 bg-gradient-to-r from-primary to-purple-500 rounded-full opacity-0 group-hover:opacity-10 blur-xl transition-opacity duration-500 -z-10"></div>
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInLeft {
          from {
            opacity: 0;
            transform: translateX(-30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        @keyframes fadeInRight {
          from {
            opacity: 0;
            transform: translateX(30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        .animate-fade-in-left {
          animation: fadeInLeft 0.8s ease-out;
        }
        .animate-fade-in-right {
          animation: fadeInRight 0.8s ease-out;
        }
      `}</style>
    </section>
  );
};

export default Hero;
